def count_journ(a)
	puts "il y a #{a.length} journalistes dans le array"
end

def count_handle_with_num(a)
	puts " Il y a #{a.grep(/[0-9]/).count} handles contenant au moins un numéro"
end

def count_handle_with_aude(a)
	a.length.times do |i|
		a[i]=a[i].downcase
	end
	puts "Il y a #{a.grep(/aude/).count} handles contenant les lettre de Aude à la suite"
end

def count_handle_startingwithmaj(a)
	#COMMENT RETIRER LES CARACTERES SPECIAUX DE CE CODE?...

	#x=0
	#a.length.times do |i|
	#	if a[i][1].upcase==a[i][1]
	#		x+=1
	#	end
	#end
	#puts " Il y a #{x} handle qui commencent par une majuscule"

	puts "Il y a #{a.grep(/^(@[A-Z])/).count} handles qui commencent par une majuscule"
end


def count_handle_withmaj(a)
	puts "Il y a #{a.grep(/[A-Z]/).count} handle qui contiennent au moins une majuscule"
end



def count_handle_with_(a)
	puts "Il y a #{a.grep(/_/).count} handle qui contiennent au moins un underscore"
end


def sorta(a)
	a=a.sort
	puts a
end


def sort50shortest(a)
	a=a.sort_by(&:length)
	for i in 0..49 do
		puts a[i]
	end
end

def place_de_epenser(a)
	# CETTE METHODE NE MARCHE PAS
	i=0
	while a[i]!="@epenser"
		i=+1
	break if a[i]=="@epenser"
	end
	puts " Le handle @epenser est à la #{i-1} ème place de la liste"		
end


